import { useState } from 'react';
import styles from './inputimage.module.css';

const ImageUpload = () => {
    const [imageBase64, setImageBase64] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = function (e) {
                const base64String = e.target.result;
                setImageBase64(base64String);
                setImagePreview(base64String);
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <div className={styles.container}>
            <label htmlFor="imageInput" className={styles.uploadBox}>
                <span className={styles.uploadLabel}>Click to upload an image</span>
            </label>
            <input
                type="file"
                id="imageInput"
                accept="image/*"
                onChange={handleImageUpload}
            />
            {imagePreview && (
                <img src={imagePreview} alt="Image Preview" className={styles.previewImage} />
            )}
            {imageBase64 && (
                <textarea readOnly value={imageBase64} className={styles.textarea} />
            )}
        </div>
    );
};

export default ImageUpload;
