import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'; // Import CSS của Quill

const RichTextEditor = () => {
    const [content, setContent] = useState('');

    // Cấu hình toolbar với các nút cần thiết
    const modules = {
        toolbar: [
            [{ 'font': [] }, { 'size': [] }],          // Chọn font và kích thước chữ
            ['bold', 'italic', 'underline', 'strike'], // In đậm, in nghiêng, gạch chân, gạch ngang
            [{ 'color': [] }, { 'background': [] }],   // Chọn màu chữ và màu nền
            [{ 'script': 'sub' }, { 'script': 'super' }], // Chỉ số dưới, chỉ số trên
            ['blockquote', 'code-block'],              // Trích dẫn, đoạn mã
            [{ 'list': 'ordered' }, { 'list': 'bullet' }], // Danh sách có thứ tự, không thứ tự
            [{ 'align': [] }],                        // Canh chỉnh văn bản
            ['link', 'image'],                        // Chèn liên kết và hình ảnh
            ['clean']                                 // Nút để xóa định dạng
        ]
    };

    // Xử lý nội dung được nhập
    const handleContentChange = (value) => {
        setContent(value);
    };

    return (
        <div>
            <h3>Rich Text Editor</h3>
            <ReactQuill
                value={content}
                onChange={handleContentChange}
                modules={modules}
                placeholder="Input your text here..."
            />
        </div>
    );
};

export default RichTextEditor;
