import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css'; // Import CSS cho ReactQuill

const RichTextEditor = () => {
    const [editorContent, setEditorContent] = useState('');

    // Cấu hình các công cụ định dạng
    const modules = {
        toolbar: [
            [{ 'header': '1' }, { 'header': '2' }, { 'font': [] }],
            [{ 'list': 'ordered' }, { 'list': 'bullet' }],
            ['bold', 'italic', 'underline', 'strike'], // Các công cụ định dạng chữ
            [{ 'color': [] }, { 'background': [] }], // Màu chữ và nền
            [{ 'align': [] }],
            ['link', 'image'],
            ['clean'] // Xóa định dạng
        ]
    };

    // Cấu hình các định dạng được hỗ trợ
    const formats = [
        'header', 'font',
        'bold', 'italic', 'underline', 'strike',
        'list', 'bullet',
        'color', 'background',
        'align',
        'link', 'image'
    ];

    return (
        <div>
            <ReactQuill
                value={editorContent}
                onChange={setEditorContent}
                modules={modules} // Kích hoạt các công cụ định dạng
                formats={formats} // Định dạng hỗ trợ
                placeholder="Nhập nội dung của bạn..."
            />
        </div>
    );
};

export default RichTextEditor;
