import React, { useState } from 'react';
import dynamic from 'next/dynamic';
import 'react-quill/dist/quill.snow.css'; // Import CSS của Quill

// Sử dụng dynamic import để tránh SSR (Server-Side Rendering)
const ReactQuill = dynamic(() => import('react-quill'), { ssr: false });

const RichTextEditor = ({ value, setValue }) => {
    // Hàm xử lý thay đổi nội dung
    const handleContentChange = (content) => {
        setValue(content);
    };

    // Cấu hình các module của React Quill
    const modules = {
        toolbar: [
            [{ 'font': [] }, { 'size': [] }], // Font và kích thước
            ['bold', 'italic', 'underline', 'strike'], // Định dạng văn bản
            [{ 'color': [] }, { 'background': [] }], // Màu sắc
            [{ 'script': 'sub' }, { 'script': 'super' }], // Subscript và superscript
            ['blockquote', 'code-block'], // Blockquote và code block
            [{ 'list': 'ordered' }, { 'list': 'bullet' }], // Danh sách
            [{ 'align': [] }], // Căn chỉnh
            ['link', 'clean'], // Chèn liên kết và xóa định dạng
        ],
    };

    // Cấu hình các định dạng được hỗ trợ
    const formats = [
        'font', 'size',
        'bold', 'italic', 'underline', 'strike',
        'color', 'background',
        'script', 'blockquote', 'code-block',
        'list', 'bullet', 'align',
        'link',
    ];

    return (
        <div>
            <ReactQuill
                value={value}
                onChange={handleContentChange}
                modules={modules} // Module cấu hình toolbar
                formats={formats} // Định dạng được hỗ trợ
                placeholder="Type your text here..." // Placeholder
            />
        </div>
    );
};

export default RichTextEditor;
