import React, { useEffect, useState, useContext } from 'react'
import Head from 'next/head'

import { publicFetch } from '../../../util/fetcher'
import { GroupAuthContext } from '../../../store/groupauth'
import Layout from '../../layout'
import PageTitle from '../../page-title'
import SearchInput from '../../search-input'
import UserList from '../../user-list'
import UserItem from '../../user-list/user-item-group'
import { FetchContext } from '../../../store/fetch'
import { Spinner } from '../../icons'

const UsersManage = () => {
    const [searchTerm, setSearchTerm] = useState(null)
    const [users, setUsers] = useState(null)
    const [waitList, setWaitList] = useState(null)
    const [loading, setLoading] = useState(false)
    const { authAxios } = useContext(FetchContext)
    const { valueGroup } = useContext(GroupAuthContext)
    const fetchListWait = () => {
        if (searchTerm === null) {
            const fetchUser = async () => {
                const { data } = await publicFetch.get(`/group/${valueGroup.inGroup}/waitlistuser`)
                setWaitList(data)
            }
            fetchUser()
        }
    }
    useEffect(() => {
        fetchListWait()
        if (searchTerm === null) {
            const fetchUser = async () => {
                const { data } = await publicFetch.get(`/group/${valueGroup.inGroup}/member`)
                setUsers(data)
            }
            fetchUser()
        } else {
            const delayDebounceFn = setTimeout(async () => {
                setLoading(true)
                const { data } = await publicFetch.get(
                    searchTerm ? `/users/${searchTerm}` : `/users`
                )
                setUsers(data)
                setLoading(false)
            }, 500)

            return () => clearTimeout(delayDebounceFn)
        }
    }, [searchTerm])

    return (
        <>
            <div>
                <PageTitle title="Request to join group" borderBottom={false} />
                {!waitList && (
                    <div className="loading">
                        <Spinner />
                    </div>
                )}

                {waitList && (
                    <>
                        <UserList>
                            {waitList?.map(({ username, profilePhoto, created, id }) => (
                                <UserItem
                                    key={id}
                                    username={username}
                                    profilePhoto={profilePhoto}
                                    created={created}
                                />
                            ))}
                        </UserList>
                        {waitList.length == 0 && (
                            <p className="not-found">No users matched your search.</p>
                        )}
                    </>
                )}
            </div>
            <div>
                <PageTitle title="Member in group" borderBottom={false} />
                <SearchInput
                    placeholder="Search by user"
                    isLoading={loading}
                    autoFocus
                    autoComplete="off"
                    type="text"
                    onChange={(e) => setSearchTerm(e.target.value)}
                />

                {!users && (
                    <div className="loading">
                        <Spinner />
                    </div>
                )}

                {users && (
                    <>
                        <UserList>
                            {users?.map(({ username, profilePhoto, created, id }) => (
                                <UserItem
                                    key={id}
                                    username={username}
                                    profilePhoto={profilePhoto}
                                    created={created}
                                />
                            ))}
                        </UserList>
                        {users.length == 0 && (
                            <p className="not-found">No users matched your search.</p>
                        )}
                    </>
                )}
            </div>
        </>
    )
}

export default UsersManage
