import React, { useEffect, useState, useContext } from 'react'
import { useRouter } from 'next/router'
import Head from 'next/head'
import { GroupAuthContext } from '../../store/groupauth'
import { publicFetch } from '../../util/fetcher'
import PageTitle from '../../components/page-title'
import Layout from '../../components/layout'
import { FetchContext } from '../../store/fetch'

import QuestionWrapper from '../../components/question/question-wrapper'
import QuestionStats from '../../components/question/question-stats'
import QuestionSummary from '../../components/question/question-summary'
import ButtonGroup from '../../components/button-group'
import { Spinner } from '../../components/icons'
import styles from './stylegroup.module.css'
export async function getServerSideProps(context) {
    const { group } = context.params;

    return {
        props: { name: group },
    };
}
const GroupDetail = ({ name }) => {
    const { authAxios } = useContext(FetchContext)
    const router = useRouter()
    const [questions, setQuestions] = useState(null)
    const [sortType, setSortType] = useState('Votes')
    const [btnText, setBtnText] = useState('Join group')
    const { valueGroup, setValueGroup } = useContext(GroupAuthContext)
    const handleJoin = () => {
        try {
            authAxios.post(`/group/${valueGroup}/joingroup`)
            setBtnText('Requested');
        } catch { }
    }
    useEffect(() => {
        try {
            const fetchGroup = async () => {
                const { groupValidate } = authAxios.get(`/group/${name}`)
                if (groupValidate.isWaiting) {
                    setBtnText('Requested')
                }
            }
            const fetchQuestion = async () => {
                const { data } = await publicFetch.get(`/group/${name}/question`)
                // const { data } = authAxios.get(`/group/${name}/question`)
                setQuestions(data)
            }
            const fetchQuestionByTag = async () => {
                const { data } = await publicFetch.get(`/questions/${router.query.tag}`)
                setQuestions(data)
            }

            if (router.query.tag) {
                fetchQuestionByTag()
            } else {
                fetchQuestion()
            }
            setValueGroup(name)
            fetchGroup()
        }
        catch (error) { }
    }, [router.query.tag])
    const handleSorting = () => {
        switch (sortType) {
            case 'Votes':
                return (a, b) => b.score - a.score
            case 'Views':
                return (a, b) => b.views - a.views
            case 'Newest':
                return (a, b) => new Date(b.created) - new Date(a.created)
            case 'Oldest':
                return (a, b) => new Date(a.created) - new Date(b.created)
            default:
                break
        }
    }
    return (
        <>
            <Head>
                <title>Group {valueGroup}</title>
            </Head>
            <Layout extra={true}>
                <PageTitle title={name} linkRoute={'/groups/ask'} adminBtn={true} borderBottom={false} />
                <div className={styles.buttonContainer}><button className={styles.btndatcauhoi} onClick={() => handleJoin()}>{btnText}</button></div>
                <ButtonGroup
                    borderBottom
                    buttons={['Votes', 'Views', 'Newest', 'Oldest']}
                    selected={sortType}
                    setSelected={setSortType}
                />
                {!questions && (
                    <div className="loading">
                        <Spinner />
                    </div>
                )}

                {questions
                    ?.sort(handleSorting())
                    .map(
                        ({
                            id,
                            votes,
                            answers,
                            views,
                            title,
                            text,
                            tags,
                            author,
                            created
                        }) => (
                            <QuestionWrapper key={id}>
                                <QuestionStats
                                    voteCount={votes.length}
                                    answerCount={answers.length}
                                    view={views}
                                />
                                <QuestionSummary
                                    id={id}
                                    title={title}
                                    tags={tags}
                                    author={author}
                                    createdTime={created}
                                >
                                    <div dangerouslySetInnerHTML={{ __html: text }} />
                                </QuestionSummary>
                            </QuestionWrapper>
                        )
                    )}
            </Layout>
        </>
    )
}
export default GroupDetail
